import React, { useState, useEffect, useRef } from 'react';
import { motion } from 'framer-motion';
import { useResponsive } from '../../hooks';
import { ScrollReveal } from '../animations/ScrollReveal';
import { siteConfig } from '../../data';

interface CursorRevealHeroProps {
  onExplorePressed?: () => void;
}

export const CursorRevealHero: React.FC<CursorRevealHeroProps> = ({ onExplorePressed }) => {
  const { isMobile, isDesktop } = useResponsive();
  const [isHovering, setIsHovering] = useState(false);
  const sectionRef = useRef<HTMLElement | null>(null);
  const rAFRef = useRef<number | null>(null);
  const revealRadiusRef = useRef(0);

  // Mouse tracking for reveal effect
  useEffect(() => {
    if (!isDesktop) return;
    const el = sectionRef.current;
    if (!el) return;

    const update = (e: MouseEvent) => {
      const rect = el.getBoundingClientRect();
      const x = e.clientX - rect.left;
      const y = e.clientY - rect.top;

      // Animate reveal radius
      const targetRadius = isHovering ? 300 : 0;
      revealRadiusRef.current += (targetRadius - revealRadiusRef.current) * 0.15;

      if (rAFRef.current) cancelAnimationFrame(rAFRef.current);
      rAFRef.current = requestAnimationFrame(() => {
        el.style.setProperty('--mx', x + 'px');
        el.style.setProperty('--my', y + 'px');
        el.style.setProperty('--reveal-radius', revealRadiusRef.current.toFixed(1) + 'px');
      });
    };

    el.addEventListener('mousemove', update);
    return () => {
      el.removeEventListener('mousemove', update);
      if (rAFRef.current) cancelAnimationFrame(rAFRef.current);
    };
  }, [isDesktop, isHovering]);

  const handleMouseEnter = () => {
    setIsHovering(true);
  };

  const handleMouseLeave = () => {
    setIsHovering(false);
  };

  const getResponsiveFontSize = (base: number) => {
    if (isMobile) return base * 0.5;
    return base;
  };

  return (
    <section 
      ref={sectionRef} 
      className="relative h-screen w-full overflow-hidden bg-[#2a2a2a]"
      onMouseEnter={handleMouseEnter}
      onMouseLeave={handleMouseLeave}
    >
      {/* Background Image (Foreground.jpg) */}
      <div className="absolute inset-0 pointer-events-none">
        <img
          src="/images/hero/Foreground.jpg"
          alt="Background"
          className="w-full h-full object-cover"
        />
        <div className="absolute inset-0 bg-gradient-to-b from-black/20 via-black/40 to-black/60" />
      </div>

      {/* Masked Foreground Image (Background.jpg) - Only visible in cursor reveal */}
      {isDesktop && (
        <div
          className="absolute inset-0 pointer-events-none"
          style={{
            WebkitMaskImage: 'radial-gradient(circle var(--reveal-radius,0px) at var(--mx,50%) var(--my,50%), #000 0%, #000 70%, transparent 100%)',
            maskImage: 'radial-gradient(circle var(--reveal-radius,0px) at var(--mx,50%) var(--my,50%), #000 0%, #000 70%, transparent 100%)'
          }}
        >
          <img
            src="/images/hero/Background.jpg"
            alt="Revealed Background"
            className="w-full h-full object-cover"
          />
          <div className="absolute inset-0 bg-gradient-to-br from-orange-400/20 via-transparent to-black/30" />

          {/* Low-poly Portrait */}
          <div className="absolute inset-0 flex items-center justify-center">
            <div className="w-96 h-96 relative">
              {/* Geometric Portrait SVG - You can replace this with your actual low-poly portrait */}
              <div 
                className="w-full h-full bg-gradient-to-br from-amber-600 via-orange-500 to-amber-700 rounded-full"
                style={{
                  clipPath: 'polygon(50% 0%, 80% 20%, 100% 50%, 80% 80%, 50% 100%, 20% 80%, 0% 50%, 20% 20%)'
                }}
              />
              {/* Glasses overlay */}
              <div className="absolute inset-0 flex items-center justify-center">
                <div className="w-32 h-12 border-4 border-black/60 rounded-full bg-transparent" />
              </div>
            </div>
          </div>
        </div>
      )}

      {/* Content Overlay */}
      <div className="relative z-10 flex flex-col h-full">
        {/* Main Content */}
        <div className="flex-1 flex flex-col items-center justify-center px-6 text-center">
          <div className="max-w-6xl mx-auto">
            <ScrollReveal delay={0.2}>
              <motion.h1
                className="font-display font-black text-white mb-6 leading-tight tracking-wider"
                style={{ 
                  fontSize: `${getResponsiveFontSize(64)}px`,
                  textShadow: '2px 2px 4px rgba(0,0,0,0.5)'
                }}
              >
                {siteConfig.heroTitle}
              </motion.h1>
            </ScrollReveal>

            <ScrollReveal delay={0.4}>
              <h2
                className="font-display font-light text-white mb-8 tracking-widest"
                style={{ 
                  fontSize: `${getResponsiveFontSize(42)}px`,
                  textShadow: '1px 1px 2px rgba(0,0,0,0.5)'
                }}
              >
                {siteConfig.tagline}
              </h2>
            </ScrollReveal>

            <ScrollReveal delay={0.6}>
              <p 
                className="text-white/90 mb-8 max-w-2xl mx-auto leading-relaxed tracking-wide"
                style={{ 
                  fontSize: `${getResponsiveFontSize(18)}px`,
                  textShadow: '1px 1px 2px rgba(0,0,0,0.5)'
                }}
              >
                {siteConfig.description}
              </p>
            </ScrollReveal>

            <ScrollReveal delay={0.8}>
              <div className="flex flex-col items-center space-y-6">
                <div className="flex flex-col items-center space-y-3">
                  <span className="text-white/70 text-sm tracking-wide">Scroll to explore</span>
                  <motion.div
                    className="w-0.5 h-10 bg-gradient-to-b from-white/60 to-transparent"
                    animate={{
                      scaleY: [1, 1.5, 1],
                    }}
                    transition={{
                      duration: 2,
                      repeat: Infinity,
                      ease: "easeInOut"
                    }}
                  />
                </div>
              </div>
            </ScrollReveal>
          </div>
        </div>

        {/* Bottom Section */}
        <div className="px-6 pb-8">
          <div className="max-w-7xl mx-auto">
            <div className="h-px bg-gradient-to-r from-transparent via-white/30 to-transparent mb-6" />

            <div className="flex justify-between items-center text-sm text-white/70">
              <ScrollReveal delay={0.3} direction="right">
                <span>UI UX Designer</span>
              </ScrollReveal>

              <ScrollReveal delay={0.5}>
                <span>{siteConfig.description.split('\n')[0]}</span>
              </ScrollReveal>

              <ScrollReveal delay={0.7} direction="left">
                <span>Flutter Engineer</span>
              </ScrollReveal>
            </div>
          </div>
        </div>
      </div>

      {/* Noise Overlay */}
      <div className="absolute inset-0 opacity-5 pointer-events-none bg-noise" />
    </section>
  );
};

export default CursorRevealHero;
