import React, { useState, useEffect, useRef } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { useResponsive } from '../../hooks';
import { ScrollReveal } from '../animations/ScrollReveal';
import { projects } from '../../data';
import { Project } from '../../types';

export const WorksSection: React.FC = () => {
  const { isMobile } = useResponsive();
  const [headerScrollOffset, setHeaderScrollOffset] = useState(0);
  const textRef = useRef<HTMLDivElement>(null);
  const sectionRef = useRef<HTMLElement>(null);

  // Enhanced parallax effect for the header text - matching portfolio-1
  useEffect(() => {
    const handleScroll = () => {
      if (!sectionRef.current) return;

      const rect = sectionRef.current.getBoundingClientRect();
      const scrollProgress = Math.max(0, Math.min(1, (window.innerHeight - rect.top) / (window.innerHeight + rect.height)));

      // Calculate scroll offset to match portfolio-1 behavior
      const textWidth = textRef.current ? textRef.current.scrollWidth : 0;
      const screenWidth = window.innerWidth;
      const maxScrollDistance = textWidth > screenWidth ? (textWidth - screenWidth + 160) * 1.1 : 0;

      setHeaderScrollOffset(scrollProgress * maxScrollDistance);
    };

    window.addEventListener('scroll', handleScroll, { passive: true });
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  return (
    <section ref={sectionRef} id="works" className="w-full">
      {/* Header Section with Paper Background - exact match to portfolio-1 */}
      <div 
        className={`relative w-full ${isMobile ? 'h-[50vh]' : 'h-[70vh]'} overflow-hidden`}
        style={{
          backgroundImage: 'url(/images/paper.png)', // Use actual paper.png from assets
          backgroundSize: 'cover',
