import React, { useState, useEffect } from 'react';
import { motion } from 'framer-motion';
import { useMousePosition, useResponsive } from '../../hooks';
import { ScrollReveal } from '../animations/ScrollReveal';
import { siteConfig } from '../../data';

interface CursorRevealHeroProps {
  onExplorePressed?: () => void;
}

interface BackgroundTheme {
  id: string;
  name: string;
  background: string;
  portrait: string;
  color: string;
  description: string;
}

const backgroundThemes: BackgroundTheme[] = [
  {
    id: 'designer',
    name: 'UI/UX Designer',
    background: '/images/hero/Background.jpg',
    portrait: '/images/hero/portraits/profile1.png',
    color: '#667eea',
    description: 'Creating beautiful and intuitive user experiences'
  },
  {
    id: 'developer',
    name: 'Full Stack Developer', 
    background: '/images/hero/Foreground.jpg',
    portrait: '/images/hero/portraits/profile2.png',
    color: '#10b981',
    description: 'Building robust and scalable applications'
  },
  {
    id: 'flutter',
    name: 'Flutter Engineer',
    background: '/images/hero/backgrounds/flutter.svg', 
    portrait: '/images/hero/avatar.png',
    color: '#0288d1',
    description: 'Crafting cross-platform mobile experiences'
  }
];

export const CursorRevealHero: React.FC<CursorRevealHeroProps> = ({ onExplorePressed }) => {
  const mousePosition = useMousePosition();
  const { isMobile, isDesktop } = useResponsive();
  const [isHovering, setIsHovering] = useState(false);
  const [currentTheme, setCurrentTheme] = useState(backgroundThemes[0]);
  const [revealRadius, setRevealRadius] = useState(150);

  // Switch themes based on mouse position
  useEffect(() => {
    if (!isDesktop) return;

    const screenWidth = window.innerWidth;
    const section = Math.floor((mousePosition.x / screenWidth) * backgroundThemes.length);
    const themeIndex = Math.min(Math.max(section, 0), backgroundThemes.length - 1);

    if (backgroundThemes[themeIndex].id !== currentTheme.id) {
      setCurrentTheme(backgroundThemes[themeIndex]);
    }
  }, [mousePosition.x, isDesktop, currentTheme.id]);

  // Adjust reveal radius based on movement
  useEffect(() => {
    if (!isDesktop) return;

    const baseRadius = isHovering ? 250 : 150;
    const mouseSpeed = Math.sqrt(
      Math.pow(mousePosition.x - (mousePosition.x || 0), 2) + 
      Math.pow(mousePosition.y - (mousePosition.y || 0), 2)
    );
    const dynamicRadius = Math.min(baseRadius + mouseSpeed * 0.5, 350);
    setRevealRadius(dynamicRadius);
  }, [mousePosition, isHovering, isDesktop]);

  const getResponsiveFontSize = (base: number) => {
    if (isMobile) return base * 0.6;
    return base;
  };

  return (
    <section className="relative h-screen w-full overflow-hidden bg-dark-100">
      {/* Default Background */}
      <div
        className="absolute inset-0 bg-cover bg-center opacity-40 transition-all duration-700"
        style={{ backgroundImage: `url(/images/hero/Background.jpg)` }}
      />

      {/* Dynamic Background with Cursor Reveal (Desktop Only) */}
      {isDesktop && (
        <>
          {/* Current theme background */}
          <div
            className="absolute inset-0 bg-cover bg-center transition-all duration-500"
            style={{
              backgroundImage: `url(${currentTheme.background})`,
              clipPath: `circle(${revealRadius}px at ${mousePosition.x}px ${mousePosition.y}px)`,
            }}
          />

          {/* Portrait overlay */}
          <div
            className="absolute inset-0 bg-contain bg-center bg-no-repeat transition-all duration-500 opacity-90"
            style={{
              backgroundImage: `url(${currentTheme.portrait})`,
              clipPath: `circle(${revealRadius * 0.7}px at ${mousePosition.x}px ${mousePosition.y}px)`,
              backgroundSize: 'auto 60%',
              backgroundPosition: 'center 30%',
            }}
          />
        </>
      )}

      {/* Theme Indicators (Desktop Only) */}
      {isDesktop && (
        <div className="absolute top-32 left-0 right-0 z-20 flex justify-center space-x-8">
          {backgroundThemes.map((theme, index) => (
            <motion.div
              key={theme.id}
              className={`px-4 py-2 rounded-full backdrop-blur-sm border transition-all duration-300 ${
                currentTheme.id === theme.id
                  ? 'bg-white bg-opacity-20 border-white text-white'
                  : 'bg-black bg-opacity-20 border-gray-500 text-gray-300'
              }`}
              style={{ 
                borderColor: currentTheme.id === theme.id ? theme.color : undefined,
                boxShadow: currentTheme.id === theme.id ? `0 0 20px ${theme.color}40` : undefined
              }}
              whileHover={{ scale: 1.05 }}
            >
              <span className="text-sm font-medium">{theme.name}</span>
            </motion.div>
          ))}
        </div>
      )}

      {/* Content Overlay */}
      <div className="relative z-10 flex flex-col items-center justify-center h-full px-6 text-center">
        <motion.div
          className="max-w-6xl mx-auto"
          onMouseEnter={() => setIsHovering(true)}
          onMouseLeave={() => setIsHovering(false)}
        >
          <ScrollReveal delay={0.2}>
            <motion.h1
              className="font-display font-black text-dark-900 mb-4 leading-tight"
              style={{ fontSize: `${getResponsiveFontSize(56)}px` }}
              animate={{
                scale: [1, 1.02, 1],
              }}
              transition={{
                duration: 6,
                repeat: Infinity,
                ease: "easeInOut"
              }}
            >
              {siteConfig.heroTitle}
            </motion.h1>
          </ScrollReveal>

          <ScrollReveal delay={0.4}>
            <h2
              className="font-display font-light text-dark-900 mb-8 tracking-wider"
              style={{ fontSize: `${getResponsiveFontSize(42)}px` }}
            >
              {siteConfig.tagline}
            </h2>
          </ScrollReveal>

          <ScrollReveal delay={0.6}>
            <p className="text-dark-800 text-lg mb-8 max-w-2xl mx-auto leading-relaxed tracking-wide">
              {siteConfig.description}
            </p>
          </ScrollReveal>

          {/* Dynamic Theme Description (Desktop Only) */}
          {isDesktop && (
            <ScrollReveal delay={0.7}>
              <motion.p 
                key={currentTheme.id}
                className="text-lg mb-8 max-w-xl mx-auto leading-relaxed font-medium"
                style={{ color: currentTheme.color }}
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: 1, y: 0 }}
                transition={{ duration: 0.5 }}
              >
                {currentTheme.description}
              </motion.p>
            </ScrollReveal>
          )}

          {/* Mobile Theme Showcase */}
          {isMobile && (
            <ScrollReveal delay={0.7}>
              <div className="flex flex-col space-y-4 mb-8">
                {backgroundThemes.map((theme, index) => (
                  <motion.div
                    key={theme.id}
                    className="flex items-center space-x-4 p-4 rounded-lg backdrop-blur-sm bg-white bg-opacity-10"
                    initial={{ opacity: 0, x: -20 }}
                    animate={{ opacity: 1, x: 0 }}
                    transition={{ delay: index * 0.1 }}
                  >
                    <div 
                      className="w-12 h-12 rounded-full"
                      style={{ backgroundColor: theme.color }}
                    />
                    <div className="text-left">
                      <h4 className="text-white font-semibold">{theme.name}</h4>
                      <p className="text-gray-300 text-sm">{theme.description}</p>
                    </div>
                  </motion.div>
                ))}
              </div>
            </ScrollReveal>
          )}

          <ScrollReveal delay={0.8}>
            <div className="flex flex-col items-center space-y-6">
              <motion.button
                onClick={onExplorePressed}
                className="btn-primary text-lg px-8 py-4"
                whileHover={{ scale: 1.05 }}
                whileTap={{ scale: 0.95 }}
              >
                Explore My Work
              </motion.button>

              <div className="flex flex-col items-center space-y-3">
                <span className="text-dark-700 text-sm tracking-wide">Scroll to explore</span>
                <motion.div
                  className="w-0.5 h-10 bg-gradient-to-b from-accent to-transparent"
                  animate={{
                    scaleY: [1, 1.5, 1],
                  }}
                  transition={{
                    duration: 2,
                    repeat: Infinity,
                    ease: "easeInOut"
                  }}
                />
              </div>
            </div>
          </ScrollReveal>
        </motion.div>

        {/* Bottom Section */}
        <div className="absolute bottom-8 left-0 right-0 px-6">
          <div className="max-w-7xl mx-auto">
            <div className="h-px bg-gradient-to-r from-transparent via-accent/30 to-transparent mb-6" />

            <div className="flex justify-between items-center text-sm text-dark-700">
              <ScrollReveal delay={0.3} direction="right">
                <span>{siteConfig.title}</span>
              </ScrollReveal>

              <ScrollReveal delay={0.5}>
                <span>Based in {siteConfig.description.split(',')[0] || 'Remote'}</span>
              </ScrollReveal>

              <ScrollReveal delay={0.7} direction="left">
                <span>Available for Projects</span>
              </ScrollReveal>
            </div>
          </div>
        </div>
      </div>

      {/* Cursor Movement Hint (Desktop Only) */}
      {isDesktop && (
        <motion.div
          className="absolute bottom-20 left-0 right-0 z-20 flex justify-center"
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ delay: 2, duration: 1 }}
        >
          <div className="flex items-center space-x-3 px-6 py-3 rounded-full backdrop-blur-sm bg-black bg-opacity-20 border border-white border-opacity-30">
            <motion.div
              className="w-2 h-2 bg-white rounded-full"
              animate={{ 
                x: [0, 15, -15, 0],
                y: [0, -10, 10, 0]
              }}
              transition={{
                duration: 3,
                repeat: Infinity,
                ease: "easeInOut"
              }}
            />
            <span className="text-white text-sm">Move cursor to explore different modes</span>
          </div>
        </motion.div>
      )}

      {/* Noise Overlay */}
      <div className="absolute inset-0 opacity-5 pointer-events-none bg-noise" />
    </section>
  );
};
