import React, { useEffect, useState } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { LoadingStage } from '../../types';

interface CurtainLoaderProps {
  onComplete: () => void;
  duration?: number;
}

export const CurtainLoader: React.FC<CurtainLoaderProps> = ({ 
  onComplete, 
  duration = 2500 
}) => {
  const [stage, setStage] = useState<LoadingStage>('initial');
  const [progress, setProgress] = useState(0);

  useEffect(() => {
    const timer1 = setTimeout(() => setStage('loading'), 500);
    const timer2 = setTimeout(() => {
      setStage('complete');
      setTimeout(onComplete, 800);
    }, duration);

    const interval = setInterval(() => {
      setProgress((prev) => {
        const currentProgress = prev + (100 / (duration / 50));
        return currentProgress >= 100 ? 100 : currentProgress;
      });

      if (currentProgress >= 100) {
        clearInterval(interval);
        setTimeout(onComplete, 500); // Delay before calling onComplete
      }
    }, 50); // Update progress every 50ms

    return () => {
      clearTimeout(timer1);
      clearTimeout(timer2);
      clearInterval(interval);
    };
  }, [onComplete, duration]);

  return (
    <AnimatePresence>
      {stage !== 'complete' && (
        <motion.div
          className="fixed inset-0 z-100 bg-dark-100"
          initial={{ opacity: 1 }}
          exit={{ opacity: 0 }}
          transition={{ duration: 0.5 }}
        >
          {/* Left Curtain */}
          <motion.div
            className="absolute top-0 left-0 w-1/2 h-full bg-dark-100"
            initial={{ x: 0 }}
            animate={stage === 'loading' ? { x: 0 } : { x: '-100%' }}
            transition={{ duration: 1.5, ease: [0.76, 0, 0.24, 1] }}
          />

          {/* Right Curtain */}
          <motion.div
            className="absolute top-0 right-0 w-1/2 h-full bg-dark-100"
            initial={{ x: 0 }}
            animate={stage === 'loading' ? { x: 0 } : { x: '100%' }}
            transition={{ duration: 1.5, ease: [0.76, 0, 0.24, 1] }}
          />

          {/* Logo and Loading Text */}
          <motion.div
            className="absolute inset-0 flex flex-col items-center justify-center"
            initial={{ opacity: 0, scale: 0.8 }}
            animate={stage === 'loading' ? { opacity: 1, scale: 1 } : { opacity: 0, scale: 0.8 }}
            transition={{ duration: 1.2, ease: 'easeOut' }}
          >
            <div className="mb-6">
              <h1
                className="text-6xl font-display font-bold text-accent"
                style={{
                  textShadow: '2px 2px 0px #1A1F2B, 4px 4px 0px #252a3a, 6px 6px 0px #2f3441'
                }}
              >
                Rohan Katale
              </h1>
            </div>
            <div className="w-16 h-0.5 bg-gradient-to-r from-transparent via-accent to-transparent mb-4" />
            <motion.p
              className="text-dark-900 text-sm tracking-widest"
              animate={{ opacity: [0.5, 1, 0.5] }}
              transition={{ duration: 2, repeat: Infinity }}
            >
              LOADING
            </motion.p>
            <div className="w-full bg-gray-200 rounded-full h-2.5 dark:bg-gray-700 mt-4">
              <div className="bg-blue-600 h-2.5 rounded-full" style={{ width: `${progress}%` }}></div>
            </div>
          </motion.div>
        </motion.div>
      )}
    </AnimatePresence>
  );
};
