import React, { useEffect, useState } from 'react';
import { motion, AnimatePresence } from 'framer-motion';

interface CurtainLoaderProps {
  onComplete: () => void;
  duration?: number;
}

export const CurtainLoader: React.FC<CurtainLoaderProps> = ({ 
  onComplete, 
  duration = 2500 
}) => {
  const [stage, setStage] = useState<'initial' | 'loading' | 'complete'>('initial');

  useEffect(() => {
    const timer1 = setTimeout(() => setStage('loading'), 500);
    const timer2 = setTimeout(() => {
      setStage('complete');
      setTimeout(onComplete, 800);
    }, duration);

    return () => {
      clearTimeout(timer1);
      clearTimeout(timer2);
    };
  }, [onComplete, duration]);

  return (
    <AnimatePresence>
      {stage !== 'complete' && (
        <motion.div
          className="fixed inset-0 z-100 bg-dark-100"
          initial={{ opacity: 1 }}
          exit={{ opacity: 0 }}
          transition={{ duration: 0.5 }}
        >
          {/* Left Curtain */}
          <motion.div
            className="absolute top-0 left-0 w-1/2 h-full bg-dark-100"
            initial={{ x: 0 }}
            animate={stage === 'complete' ? { x: '-100%' } : { x: 0 }}
            transition={{ duration: 1.5, ease: [0.76, 0, 0.24, 1] }}
          />

          {/* Right Curtain */}
          <motion.div
            className="absolute top-0 right-0 w-1/2 h-full bg-dark-100"
            initial={{ x: 0 }}
            animate={stage === 'complete' ? { x: '100%' } : { x: 0 }}
            transition={{ duration: 1.5, ease: [0.76, 0, 0.24, 1] }}
          />

          {/* Logo and Loading Text */}
          <motion.div
            className="absolute inset-0 flex flex-col items-center justify-center"
            initial={{ opacity: 0, scale: 0.8 }}
            animate={stage === 'loading' ? { opacity: 1, scale: 1 } : { opacity: 0, scale: 0.8 }}
            transition={{ duration: 1.2, ease: 'easeOut' }}
          >
            <div className="w-24 h-24 mb-6 bg-dark-900 rounded-lg flex items-center justify-center">
              <span className="text-2xl font-bold text-accent">R</span>
            </div>
            <div className="w-16 h-0.5 bg-gradient-to-r from-transparent via-accent to-transparent mb-4" />
            <motion.p
              className="text-dark-900 text-sm tracking-widest"
              animate={{ opacity: [0.5, 1, 0.5] }}
              transition={{ duration: 2, repeat: Infinity }}
            >
              LOADING
            </motion.p>
          </motion.div>
        </motion.div>
      )}
    </AnimatePresence>
  );
};
