import React, { useState, useEffect, useRef } from 'react';
import { motion } from 'framer-motion';
import { useMousePosition, useResponsive } from '../../hooks';
import { ScrollReveal } from '../animations/ScrollReveal';
import { siteConfig } from '../../data';

interface CursorRevealHeroProps {
  onExplorePressed?: () => void;
}

interface BackgroundTheme {
  id: string;
  name: string;
  background: string;
  color: string;
  description: string;
}

const backgroundThemes: BackgroundTheme[] = [
  { id: 'design', name: 'Design Focus', background: '/images/hero/Background.jpg', color: '#667eea', description: 'Crafting clean, accessible interfaces' },
  { id: 'frontend', name: 'Frontend Build', background: '/images/hero/Foreground.jpg', color: '#10b981', description: 'Implementing responsive, performant UI' },
  { id: 'systems', name: 'System Logic', background: '/images/hero/backgrounds/flutter.svg', color: '#0288d1', description: 'Structuring scalable architecture' },
];

interface PortraitSet {
  neutral: string;
  smile: string;
}

// Provide default expected file paths (user can replace with own assets)
const portraitSet: PortraitSet = {
  neutral: '/images/hero/portraits/rohan-neutral.webp',
  smile: '/images/hero/portraits/rohan-smile.webp'
};

export const CursorRevealHero: React.FC<CursorRevealHeroProps> = ({ onExplorePressed }) => {
  const mousePosition = useMousePosition();
  const { isMobile, isDesktop } = useResponsive();
  const [isHovering, setIsHovering] = useState(false);
  const [currentTheme, setCurrentTheme] = useState(backgroundThemes[0]);
  const [activePortrait, setActivePortrait] = useState<'neutral' | 'smile'>('neutral');
  // Replace state radius with ref to avoid re-render every frame
  const revealRadiusRef = useRef(180);
  const sectionRef = useRef<HTMLElement | null>(null);
  const rAFRef = useRef<number | null>(null);
  const lastPointRef = useRef<{x:number;y:number}|null>(null);
  const dwellTimerRef = useRef<number | null>(null);
  const hoverAreaRef = useRef<HTMLDivElement | null>(null);
  const hoverDelayTimerRef = useRef<number | null>(null);
  const revealProgressRef = useRef(0); // 0 -> 1
  const breathProgressRef = useRef(0); // 0 -> 1 looping
  const reducedMotion = useRef<boolean>(false);
  const ringRef = useRef<HTMLDivElement | null>(null);

  // Switch themes based on mouse position (still cheap)
  useEffect(() => {
    if (!isDesktop) return;
    const screenWidth = window.innerWidth;
    const section = Math.floor((mousePosition.x / screenWidth) * backgroundThemes.length);
    const themeIndex = Math.min(Math.max(section, 0), backgroundThemes.length - 1);
    if (backgroundThemes[themeIndex].id !== currentTheme.id) {
      setCurrentTheme(backgroundThemes[themeIndex]);
    }
  }, [mousePosition.x, isDesktop, currentTheme.id]);

  // Imperative mouse tracking for clip-path via CSS variables (smooth + no re-render)
  useEffect(() => {
    if (!isDesktop) return;
    const el = sectionRef.current;
    if (!el) return;

    const update = (e: MouseEvent) => {
      const rect = el.getBoundingClientRect();
      const x = e.clientX - rect.left;
      const y = e.clientY - rect.top;

      // Smooth radius adapt based on modest speed (distance from last point)
      const last = lastPointRef.current;
      let speed = 0;
      if (last) {
        const dx = x - last.x; const dy = y - last.y;
        speed = Math.min(Math.sqrt(dx*dx + dy*dy), 120); // cap
      }
      lastPointRef.current = { x, y };
      const base = isHovering ? 220 : 170;
      // ease current radius toward target (simple spring-ish)
      const target = base + speed * 0.4;
      revealRadiusRef.current += (target - revealRadiusRef.current) * 0.18;

      // Use rAF batching
      if (rAFRef.current) cancelAnimationFrame(rAFRef.current);
      rAFRef.current = requestAnimationFrame(() => {
        el.style.setProperty('--mx', x + 'px');
        el.style.setProperty('--my', y + 'px');
        el.style.setProperty('--reveal-radius', revealRadiusRef.current.toFixed(1) + 'px');
      });
    };

    el.addEventListener('mousemove', update);
    return () => {
      el.removeEventListener('mousemove', update);
      if (rAFRef.current) cancelAnimationFrame(rAFRef.current);
    };
  }, [isDesktop, isHovering]);

  // Preload portraits once
  useEffect(() => {
    Object.values(portraitSet).forEach(src => {
      const img = new Image();
      img.src = src;
    });
  }, []);

  const clearDwell = () => {
    if (dwellTimerRef.current) {
      window.clearTimeout(dwellTimerRef.current);
      dwellTimerRef.current = null;
    }
  };

  const triggerSmileAfterDwell = () => {
    clearDwell();
    dwellTimerRef.current = window.setTimeout(() => {
      setActivePortrait('smile');
    }, 260); // dwell duration
  };

  const handleEnter = () => {
    clearTimeout(hoverDelayTimerRef.current || undefined);
    hoverDelayTimerRef.current = window.setTimeout(() => {
      setIsHovering(true);
      animateReveal(1);
    }, 150); // match Flutter delayed forward
    triggerSmileAfterDwell();
  };

  const handleLeave = () => {
    clearTimeout(hoverDelayTimerRef.current || undefined);
    setIsHovering(false);
    animateReveal(0); // reverse reveal
    clearDwell();
    setActivePortrait('neutral');
  };

  // Setup reduced motion preference
  useEffect(() => {
    const mq = window.matchMedia('(prefers-reduced-motion: reduce)');
    reducedMotion.current = mq.matches;
    const listener = () => { reducedMotion.current = mq.matches; };
    mq.addEventListener('change', listener);
    return () => mq.removeEventListener('change', listener);
  }, []);

  // Easing (approx elasticOut similar to Flutter elasticOut curve segment)
  const elasticOut = (t: number) => {
    if (t === 0 || t === 1) return t;
    const p = 0.45; // period
    return Math.pow(2, -10 * t) * Math.sin((t - p / 4) * (2 * Math.PI) / p) + 1;
  };

  const animateReveal = (target: number, duration = 800) => {
    if (reducedMotion.current) {
      revealProgressRef.current = target;
      return;
    }
    const start = revealProgressRef.current;
    const delta = target - start;
    if (delta === 0) return;
    const startTime = performance.now();
    const step = (now: number) => {
      const t = Math.min(1, (now - startTime) / duration);
      const eased = target > start ? elasticOut(t) : 1 - elasticOut(1 - t);
      revealProgressRef.current = start + delta * eased;
      // radius update happens in mouse loop / breath loop, but force one immediate update if static mouse
      const el = sectionRef.current;
      if (el) {
        // Recompute with new reveal progress
        el.style.setProperty('--reveal-progress', revealProgressRef.current.toFixed(3));
      }
      if (t < 1) requestAnimationFrame(step);
    };
    requestAnimationFrame(step);
  };

  // Breathing loop (6s period like Flutter) influencing radius scale (±15%)
  useEffect(() => {
    if (!isDesktop) return;
    let raf: number;
    let lastTs = performance.now();
    const loop = (now: number) => {
      const el = sectionRef.current;
      const dt = now - lastTs;
      lastTs = now;
      if (!reducedMotion.current) {
        // 6000ms period
        breathProgressRef.current = (breathProgressRef.current + dt / 6000) % 1;
        const breathScale = 1 + Math.sin(breathProgressRef.current * Math.PI * 2) * 0.15; // matches Flutter 0.15 amplitude
        if (el) el.style.setProperty('--breath-scale', breathScale.toFixed(4));
      } else {
        if (el) el.style.setProperty('--breath-scale', '1');
      }
      // Update radius if mouse static to reflect breathing / reveal progress
      if (el) {
        const baseRadius = revealRadiusRef.current; // already smoothed to speed
        const finalRadius = baseRadius * (parseFloat(el.style.getPropertyValue('--breath-scale')) || 1) * revealProgressRef.current;
        el.style.setProperty('--reveal-radius', finalRadius.toFixed(1) + 'px');
      }
      raf = requestAnimationFrame(loop);
    };
    raf = requestAnimationFrame(loop);
    return () => cancelAnimationFrame(raf);
  }, [isDesktop]);

  // Adjust existing mouse tracking to incorporate reveal progress + breathing (breathing multiplicative applied in loop above)
  useEffect(() => {
    if (!isDesktop) return;
    const el = sectionRef.current;
    if (!el) return;

    const update = (e: MouseEvent) => {
      const rect = el.getBoundingClientRect();
      const x = e.clientX - rect.left;
      const y = e.clientY - rect.top;

      const last = lastPointRef.current;
      let speed = 0;
      if (last) {
        const dx = x - last.x; const dy = y - last.y;
        speed = Math.min(Math.sqrt(dx*dx + dy*dy), 120);
      }
      lastPointRef.current = { x, y };

      const base = 170 + (isHovering ? 50 : 0); // 170 -> 220 when hover like original base 130 * reveal
      const target = base + speed * 0.4;
      revealRadiusRef.current += (target - revealRadiusRef.current) * 0.18;

      if (rAFRef.current) cancelAnimationFrame(rAFRef.current);
      rAFRef.current = requestAnimationFrame(() => {
        el.style.setProperty('--mx', x + 'px');
        el.style.setProperty('--my', y + 'px');
        // radius now recalculated in breathing loop but also set immediate (without breath scale) for responsiveness
        const immediate = revealRadiusRef.current * revealProgressRef.current;
        el.style.setProperty('--reveal-radius', immediate.toFixed(1) + 'px');
      });

      // Update ring position transform cheaply
      if (ringRef.current) {
        ringRef.current.style.transform = `translate(${x - 20}px, ${y - 20}px)`; // center 40x40 ring
      }
    };

    el.addEventListener('mousemove', update);
    return () => {
      el.removeEventListener('mousemove', update);
      if (rAFRef.current) cancelAnimationFrame(rAFRef.current);
    };
  }, [isDesktop, isHovering]);

  // Ensure initial revealProgress CSS var
  useEffect(() => {
    const el = sectionRef.current; if (el) el.style.setProperty('--reveal-progress', '0');
  }, []);

  // Pulse ring animation (simple JS loop when hovering)
  useEffect(() => {
    if (!isDesktop) return;
    let raf: number;
    let pulseT = 0; // 0..1 over 2s
    const run = (now: number) => {
      const el = ringRef.current;
      if (el && isHovering && revealProgressRef.current > 0.05) {
        pulseT = (pulseT + 1/60 / 2) % 1; // approximate 60fps / 2s period
        const scale = 1 + Math.sin(pulseT * Math.PI * 2) * 0.2; // 1 -> 1.2
        el.style.opacity = `${revealProgressRef.current}`;
        el.style.scale = (scale * revealProgressRef.current).toString();
      } else if (el) {
        el.style.opacity = '0';
      }
      raf = requestAnimationFrame(run);
    };
    raf = requestAnimationFrame(run);
    return () => cancelAnimationFrame(raf);
  }, [isDesktop, isHovering]);

  const getResponsiveFontSize = (base: number) => {
    if (isMobile) return base * 0.6;
    return base;
  };

  return (
    <section ref={sectionRef} className="relative h-screen w-full overflow-hidden bg-[#090b10]">
      {/* Neutral Base Background (outside reveal) */}
      <div className="absolute inset-0 pointer-events-none">
        <div className="absolute inset-0 bg-gradient-to-br from-[#0f141e] via-[#0c1018] to-[#06080c]" />
        <div className="absolute inset-0 mix-blend-overlay bg-[radial-gradient(circle_at_30%_40%,rgba(255,255,255,0.08),transparent_60%)]" />
      </div>

      {/* Dynamic Theme Background & Portrait (Desktop Only) */}
      {isDesktop && (
        <>
          <motion.div
            key={`bg-${currentTheme.id}`}
            className="absolute inset-0 will-change-transform"
            style={{
              backgroundImage: `url(${currentTheme.background})`,
              backgroundSize: 'cover',
              backgroundPosition: 'center',
              WebkitMaskImage: 'radial-gradient(circle var(--reveal-radius,180px) at var(--mx,50%) var(--my,50%), #000 0%, #000 60%, transparent 100%)',
              maskImage: 'radial-gradient(circle var(--reveal-radius,180px) at var(--mx,50%) var(--my,50%), #000 0%, #000 60%, transparent 100%)'
            }}
            initial={{ opacity: 0 }}
            animate={{ opacity: 0.9 }}
            transition={{ duration: 0.4 }}
          />
          {/* Portrait stack masked by same radial gradient */}
          <div
            className="absolute inset-0 pointer-events-none"
            style={{
              WebkitMaskImage: 'radial-gradient(circle var(--reveal-radius,180px) at var(--mx,50%) var(--my,50%), #000 0%, #000 60%, transparent 100%)',
              maskImage: 'radial-gradient(circle var(--reveal-radius,180px) at var(--mx,50%) var(--my,50%), #000 0%, #000 60%, transparent 100%)'
            }}
          >
            {/* Neutral */}
            <img
              src={portraitSet.neutral}
              alt="Rohan neutral portrait"
              draggable={false}
              className={`absolute top-1/2 left-1/2 -translate-x-1/2 -translate-y-1/2 select-none transition-opacity duration-300 ease-out will-change-opacity ${activePortrait === 'neutral' ? 'opacity-100' : 'opacity-0'}`}
              style={{
                width: '420px',
                maxWidth: '34vw',
                filter: 'drop-shadow(0 8px 26px rgba(0,0,0,0.55))'
              }}
            />
            {/* Smile */}
            <img
              src={portraitSet.smile}
              alt="Rohan smiling portrait"
              draggable={false}
              className={`absolute top-1/2 left-1/2 -translate-x-1/2 -translate-y-1/2 select-none transition-opacity duration-320 ease-out will-change-opacity ${activePortrait === 'smile' ? 'opacity-100' : 'opacity-0'}`}
              style={{
                width: '420px',
                maxWidth: '34vw',
                filter: 'drop-shadow(0 10px 30px rgba(0,0,0,0.6))'
              }}
            />
          </div>

          {/* Additional masked gradient/tint layers to approximate Flutter overlays */}
          <div
            className="absolute inset-0 pointer-events-none"
            style={{
              WebkitMaskImage: 'radial-gradient(circle var(--reveal-radius,0px) at var(--mx,50%) var(--my,50%), #000 0%, #000 60%, transparent 100%)',
              maskImage: 'radial-gradient(circle var(--reveal-radius,0px) at var(--mx,50%) var(--my,50%), #000 0%, #000 60%, transparent 100%)'
            }}
          >
            <div className="absolute inset-0" style={{
              background: 'radial-gradient(circle at center, rgba(255,255,255,0.08), rgba(0,0,0,0.35) 70%)'
            }} />
            <div className="absolute inset-0" style={{
              background: 'linear-gradient(to bottom, rgba(0,0,0,0.15), rgba(0,0,0,0.55))'
            }} />
            <div className="absolute inset-0 backdrop-blur-[1.5px]" style={{ background: 'rgba(255,255,255,0.02)' }} />
          </div>
        </>
      )}

      {/* Theme Indicators */}
      {isDesktop && (
        <div className="absolute top-32 left-0 right-0 z-20 flex justify-center space-x-8">
          {backgroundThemes.map((theme) => (
            <motion.div
              key={theme.id}
              className={`px-4 py-2 rounded-full backdrop-blur-sm border transition-all duration-300 ${
                currentTheme.id === theme.id
                  ? 'bg-white/15 border-white text-white'
                  : 'bg-white/5 border-white/20 text-gray-300'
              }`}
              style={{
                borderColor: currentTheme.id === theme.id ? theme.color : undefined,
                boxShadow: currentTheme.id === theme.id ? `0 0 14px ${theme.color}55` : undefined
              }}
              whileHover={{ scale: 1.05 }}
            >
              <span className="text-sm font-medium">{theme.name}</span>
            </motion.div>
          ))}
        </div>
      )}

      {/* Content Overlay */}
      <div
        className="relative z-10 flex flex-col items-center justify-center h-full px-6 text-center select-none"
        ref={hoverAreaRef}
        onMouseEnter={handleEnter}
        onMouseLeave={handleLeave}
        onMouseMove={() => {
          // if still neutral and inside, restart dwell after movement pauses
          if (activePortrait === 'neutral' && !dwellTimerRef.current) triggerSmileAfterDwell();
        }}
      >
        <motion.div
          className="max-w-6xl mx-auto"
          onMouseEnter={() => setIsHovering(true)}
          onMouseLeave={() => setIsHovering(false)}
        >
          <ScrollReveal delay={0.2}>
            <motion.h1
              className="font-display font-black text-dark-900 mb-4 leading-tight"
              style={{ fontSize: `${getResponsiveFontSize(56)}px` }}
              animate={{
                scale: [1, 1.02, 1],
              }}
              transition={{
                duration: 6,
                repeat: Infinity,
                ease: "easeInOut"
              }}
            >
              {siteConfig.heroTitle}
            </motion.h1>
          </ScrollReveal>

          <ScrollReveal delay={0.4}>
            <h2
              className="font-display font-light text-dark-900 mb-8 tracking-wider"
              style={{ fontSize: `${getResponsiveFontSize(42)}px` }}
            >
              {siteConfig.tagline}
            </h2>
          </ScrollReveal>

          <ScrollReveal delay={0.6}>
            <p className="text-dark-800 text-lg mb-8 max-w-2xl mx-auto leading-relaxed tracking-wide">
              {siteConfig.description}
            </p>
          </ScrollReveal>

          {/* Dynamic Theme Description (Desktop Only) */}
          {isDesktop && (
            <ScrollReveal delay={0.7}>
              <motion.p 
                key={currentTheme.id}
                className="text-lg mb-8 max-w-xl mx-auto leading-relaxed font-medium"
                style={{ color: currentTheme.color }}
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: 1, y: 0 }}
                transition={{ duration: 0.5 }}
              >
                {currentTheme.description}
              </motion.p>
            </ScrollReveal>
          )}

          {/* Mobile Theme Showcase */}
          {isMobile && (
            <ScrollReveal delay={0.7}>
              <div className="flex flex-col space-y-4 mb-8">
                {backgroundThemes.map((theme, index) => (
                  <motion.div
                    key={theme.id}
                    className="flex items-center space-x-4 p-4 rounded-lg backdrop-blur-sm bg-white bg-opacity-10"
                    initial={{ opacity: 0, x: -20 }}
                    animate={{ opacity: 1, x: 0 }}
                    transition={{ delay: index * 0.1 }}
                  >
                    <div 
                      className="w-12 h-12 rounded-full"
                      style={{ backgroundColor: theme.color }}
                    />
                    <div className="text-left">
                      <h4 className="text-white font-semibold">{theme.name}</h4>
                      <p className="text-gray-300 text-sm">{theme.description}</p>
                    </div>
                  </motion.div>
                ))}
              </div>
            </ScrollReveal>
          )}

          <ScrollReveal delay={0.8}>
            <div className="flex flex-col items-center space-y-6">
              <motion.button
                onClick={onExplorePressed}
                className="btn-primary text-lg px-8 py-4"
                whileHover={{ scale: 1.05 }}
                whileTap={{ scale: 0.95 }}
              >
                Explore My Work
              </motion.button>

              <div className="flex flex-col items-center space-y-3">
                <span className="text-dark-700 text-sm tracking-wide">Scroll to explore</span>
                <motion.div
                  className="w-0.5 h-10 bg-gradient-to-b from-accent to-transparent"
                  animate={{
                    scaleY: [1, 1.5, 1],
                  }}
                  transition={{
                    duration: 2,
                    repeat: Infinity,
                    ease: "easeInOut"
                  }}
                />
              </div>
            </div>
          </ScrollReveal>
        </motion.div>

        {/* Bottom Section */}
        <div className="absolute bottom-8 left-0 right-0 px-6">
          <div className="max-w-7xl mx-auto">
            <div className="h-px bg-gradient-to-r from-transparent via-accent/30 to-transparent mb-6" />

            <div className="flex justify-between items-center text-sm text-dark-700">
              <ScrollReveal delay={0.3} direction="right">
                <span>{siteConfig.title}</span>
              </ScrollReveal>

              <ScrollReveal delay={0.5}>
                <span>Based in {siteConfig.description.split(',')[0] || 'Remote'}</span>
              </ScrollReveal>

              <ScrollReveal delay={0.7} direction="left">
                <span>Available for Projects</span>
              </ScrollReveal>
            </div>
          </div>
        </div>
      </div>

      {/* Cursor Movement Hint (Desktop Only) */}
      {isDesktop && (
        <motion.div
          className="absolute bottom-20 left-0 right-0 z-20 flex justify-center"
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ delay: 2, duration: 1 }}
        >
          <div className="flex items-center space-x-3 px-6 py-3 rounded-full backdrop-blur-sm bg-white/10 border border-white/30">
            <motion.div
              className="w-2 h-2 bg-white rounded-full"
              animate={{ x: [0, 30, -30, 0] }}
              transition={{ duration: 4, repeat: Infinity, ease: 'easeInOut' }}
            />
            <span className="text-white text-sm">Move cursor to reveal modes</span>
          </div>
        </motion.div>
      )}

      {/* Cursor ring indicator (after content to sit on top) */}
      {isDesktop && (
        <div
          ref={ringRef}
          className="pointer-events-none absolute top-0 left-0 z-40 w-10 h-10 rounded-full border-2 border-accent/70 shadow-[0_0_16px_rgba(0,210,255,0.35)] opacity-0 will-change-transform"
        >
          <div className="w-full h-full rounded-full bg-accent/15 backdrop-blur-[3px]" />
        </div>
      )}

      {/* Noise Overlay */}
      <div className="absolute inset-0 opacity-5 pointer-events-none bg-noise" />
    </section>
  );
};
