import React, { useState, useEffect, useRef } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { useResponsive } from '../../hooks';
import { ScrollReveal } from '../animations/ScrollReveal';
import { projects } from '../../data';
import { Project } from '../../types';

export const WorksSection: React.FC = () => {
  const { isMobile } = useResponsive();
  const [headerScrollOffset, setHeaderScrollOffset] = useState(0);
  const textRef = useRef<HTMLDivElement>(null);
  const sectionRef = useRef<HTMLElement>(null);

  // Enhanced parallax effect for the header text - matching portfolio-1
  useEffect(() => {
    const handleScroll = () => {
      if (!sectionRef.current) return;

      const rect = sectionRef.current.getBoundingClientRect();
      const scrollProgress = Math.max(0, Math.min(1, (window.innerHeight - rect.top) / (window.innerHeight + rect.height)));

      // Calculate scroll offset to match portfolio-1 behavior
      const textWidth = textRef.current ? textRef.current.scrollWidth : 0;
      const screenWidth = window.innerWidth;
      const maxScrollDistance = textWidth > screenWidth ? (textWidth - screenWidth + 160) * 1.1 : 0;

      setHeaderScrollOffset(scrollProgress * maxScrollDistance);
    };

    window.addEventListener('scroll', handleScroll, { passive: true });
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  return (
    <section ref={sectionRef} id="works" className="w-full">
      {/* Header Section with Paper Background - exact match to portfolio-1 */}
      <div 
        className={`relative w-full ${isMobile ? 'h-[50vh]' : 'h-[70vh]'} overflow-hidden`}
        style={{
          backgroundImage: 'url(/images/paper.png)', // Use actual paper.png from assets
          backgroundSize: 'cover',
          backgroundPosition: 'center'
        }}
      >
        {/* Scrolling Text Section */}
        <div className={`${isMobile ? 'h-[35vh]' : 'h-[65vh]'} overflow-hidden flex items-center`}>
          <div className="w-full overflow-hidden">
            <div
              ref={textRef}
              className="whitespace-nowrap font-display font-black select-none"
              style={{
                fontSize: isMobile ? '80px' : '180px',
                color: '#000000', // Black text like portfolio-1
                transform: `translateX(-${headerScrollOffset}px)`,
                paddingLeft: isMobile ? '12px' : '20px',
                lineHeight: '1'
              }}
            >
              DESIGNED WITH LOGIC DESIGNED WITH LOGIC DESIGNED WITH LOGIC
            </div>
          </div>
        </div>

        {/* Bottom Header Content */}
        <div className="absolute bottom-0 left-0 right-0">
          <div className={`px-6 pb-6 ${isMobile ? 'px-4 pb-4' : ''}`}>
            <div className="max-w-7xl mx-auto">
              {isMobile ? (
                <div className="space-y-2">
                  <h2 className="text-black font-bold text-base">(Works.)</h2>
                  <p className="text-black font-bold text-xs text-center">
                    This creation is a confession, written in dark and dart.
                  </p>
                </div>
              ) : (
                <div className="flex justify-between items-end">
                  <div className="w-[15%]">
                    <h2 className="text-black font-bold text-lg">(Works.)</h2>
                  </div>
                  <div className="flex-1 text-center">
                    <p className="text-black font-bold text-base">
                      This creation is a confession, written in dark and dart.
                    </p>
                  </div>
                  <div className="w-[15%]"></div>
                </div>
              )}
            </div>
          </div>
        </div>
      </div>

      {/* Projects Section */}
      <div>
        {projects.map((project, index) => (
          <ProjectSection
            key={project.id}
            project={project}
            index={index}
          />
        ))}
      </div>
    </section>
  );
};

interface ProjectSectionProps {
  project: Project;
  index: number;
}

const ProjectSection: React.FC<ProjectSectionProps> = ({ project, index }) => {
  const { isMobile } = useResponsive();

  if (isMobile) {
    return <MobileProjectSection project={project} index={index} />;
  }

  // Determine project type based on project.type or alternating pattern
  const isMobileProject = project.type === 'mobile';

  return isMobileProject ? (
    <DesktopMobileProjectSection project={project} index={index} />
  ) : (
    <DesktopProjectSection project={project} index={index} />
  );
};

// Desktop layout for regular projects (like portfolio-1's _buildDesktopLayout)
const DesktopProjectSection: React.FC<{ project: Project; index: number }> = ({ project, index }) => {
  return (
    <div 
      className="h-screen w-full relative"
      style={{
        backgroundColor: '#000000', // Black background like portfolio-1
        backgroundImage: 'url(/images/workBg.png)', // Use actual workBg.png
        backgroundSize: 'cover',
        backgroundPosition: 'center'
      }}
    >
      <div className="h-full flex items-center px-6">
        <div className="max-w-7xl mx-auto w-full flex items-center">
          {/* Left side - Project Info (15% width like portfolio-1) */}
          <div className="w-[15%] pr-8">
            <ScrollReveal delay={0.2}>
              <div className="space-y-4">
                <h3 className="text-2xl md:text-3xl font-display font-bold text-black">
                  {project.title}
                </h3>
                <p className="text-black text-base leading-relaxed">
                  {project.description}
                </p>

                {/* Project Links */}
                <div className="flex flex-col space-y-3 pt-4">
                  {project.githubUrl && (
                    <a
                      href={project.githubUrl}
                      target="_blank"
                      rel="noopener noreferrer"
                      className="inline-flex items-center space-x-2 text-black/80 hover:text-black transition-colors duration-300 text-sm font-medium"
                    >
                      <svg className="w-5 h-5" fill="currentColor" viewBox="0 0 24 24">
                        <path d="M12 0c-6.626 0-12 5.373-12 12 0 5.302 3.438 9.8 8.207 11.387.599.111.793-.261.793-.577v-2.234c-3.338.726-4.033-1.416-4.033-1.416-.546-1.387-1.333-1.756-1.333-1.756-1.089-.745.083-.729.083-.729 1.205.084 1.839 1.237 1.839 1.237 1.07 1.834 2.807 1.304 3.492.997.107-.775.418-1.305.762-1.604-2.665-.305-5.467-1.334-5.467-5.931 0-1.311.469-2.381 1.236-3.221-.124-.303-.535-1.524.117-3.176 0 0 1.008-.322 3.301 1.23.957-.266 1.983-.399 3.003-.404 1.02.005 2.047.138 3.006.404 2.291-1.552 3.297-1.23 3.297-1.23.653 1.653.242 2.874.118 3.176.77.84 1.235 1.911 1.235 3.221 0 4.609-2.807 5.624-5.479 5.921.43.372.823 1.102.823 2.222v3.293c0 .319.192.694.801.576 4.765-1.589 8.199-6.086 8.199-11.386 0-6.627-5.373-12-12-12z"/>
                      </svg>
                      <span>View Code</span>
                    </a>
                  )}
                  {project.liveUrl && (
                    <a
                      href={project.liveUrl}
                      target="_blank"
                      rel="noopener noreferrer"
                      className="inline-flex items-center space-x-2 text-black/80 hover:text-black transition-colors duration-300 text-sm font-medium"
                    >
                      <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M10 6H6a2 2 0 00-2 2v10a2 2 0 002 2h10a2 2 0 002-2v-4M14 4h6m0 0v6m0-6L10 14" />
                      </svg>
                      <span>Live Demo</span>
                    </a>
                  )}
                </div>
              </div>
            </ScrollReveal>
          </div>

          {/* Spacer */}
          <div className="w-[3%]"></div>

          {/* Right side - Project Images (82% width) */}
          <div className="flex-1 flex justify-center">
            <ScrollReveal delay={0.4}>
              <ProjectImageCarousel project={project} />
            </ScrollReveal>
          </div>
        </div>
      </div>
    </div>
  );
};

// Desktop layout for mobile projects (like portfolio-1's _buildDesktopMobileProjectLayout)
const DesktopMobileProjectSection: React.FC<{ project: Project; index: number }> = ({ project, index }) => {
  return (
    <div 
      className="h-screen w-full relative"
      style={{
        backgroundColor: '#000000', // Black background
        backgroundImage: 'url(/images/workBg.png)', // Use actual workBg.png
        backgroundSize: 'cover',
        backgroundPosition: 'center'
      }}
    >
      <div className="h-full flex items-center px-6">
        <div className="max-w-7xl mx-auto w-full flex items-center">
          {/* Left side - Project Info (18% width for mobile projects) */}
          <div className="w-[18%] pr-8">
            <ScrollReveal delay={0.2}>
              <div className="space-y-6">
                <h3 className="text-2xl md:text-3xl font-display font-bold text-black">
                  {project.title}
                </h3>
                <p className="text-black text-base leading-relaxed">
                  {project.description}
                </p>
              </div>
            </ScrollReveal>
          </div>

          {/* Right side - Mobile Project Images (centered) */}
          <div className="flex-1 flex justify-center">
            <ScrollReveal delay={0.4}>
              <MobileProjectImageCarousel project={project} />
            </ScrollReveal>
          </div>
        </div>
      </div>
    </div>
  );
};

// Mobile layout (like portfolio-1's _buildMobileLayout)
const MobileProjectSection: React.FC<{ project: Project; index: number }> = ({ project, index }) => {
  return (
    <div 
      className="min-h-screen w-full py-12 px-4"
      style={{
        backgroundColor: '#000000',
        backgroundImage: 'url(/images/workBg.png)',
        backgroundSize: 'cover',
        backgroundPosition: 'center'
      }}
    >
      <div className="space-y-8">
        <ScrollReveal delay={0.2}>
          <div className="space-y-4 text-center">
            <h3 className="text-xl font-display font-bold text-black">
              {project.title}
            </h3>
            <p className="text-black text-sm leading-relaxed">
              {project.description}
            </p>
          </div>
        </ScrollReveal>

        <ScrollReveal delay={0.4}>
          <div className="flex justify-center">
            {project.type === 'mobile' ? (
              <MobileProjectImageCarousel project={project} />
            ) : (
              <ProjectImageCarousel project={project} />
            )}
          </div>
        </ScrollReveal>
      </div>
    </div>
  );
};

const ProjectImageCarousel: React.FC<{ project: Project }> = ({ project }) => {
  const [currentImage, setCurrentImage] = useState(0);
  const [isAutoPlaying, setIsAutoPlaying] = useState(true);
  const { isMobile } = useResponsive();

  useEffect(() => {
    if (!isAutoPlaying) return;

    const interval = setInterval(() => {
      setCurrentImage((prev) => (prev + 1) % project.images.length);
    }, 3000);

    return () => clearInterval(interval);
  }, [isAutoPlaying, project.images.length]);

  const nextImage = () => {
    setCurrentImage((prev) => (prev + 1) % project.images.length);
    setIsAutoPlaying(false);
  };

  const prevImage = () => {
    setCurrentImage((prev) => (prev - 1 + project.images.length) % project.images.length);
    setIsAutoPlaying(false);
  };

  return (
    <motion.div
      className="relative group"
      initial={{ opacity: 0, scale: 0.9 }}
      whileInView={{ opacity: 1, scale: 1 }}
      viewport={{ once: true }}
      transition={{ duration: 0.8 }}
      whileHover={{ scale: 1.02 }}
      style={{
        height: isMobile ? '40vh' : '80vh',
        width: isMobile ? '85vw' : '70vw'
      }}
    >
      <div className="relative overflow-hidden rounded-2xl bg-dark-300 h-full">
        <AnimatePresence mode="wait">
          <motion.img
            key={currentImage}
            src={project.images[currentImage]}
            alt={`${project.title} - Image ${currentImage + 1}`}
            className="w-full h-full object-contain"
            initial={{ opacity: 0, scale: 1.1 }}
            animate={{ opacity: 1, scale: 1 }}
            exit={{ opacity: 0, scale: 0.9 }}
            transition={{ duration: 0.5 }}
          />
        </AnimatePresence>

        {/* Navigation Arrows */}
        {project.images.length > 1 && (
          <>
            <button
              onClick={prevImage}
              className="absolute left-4 top-1/2 -translate-y-1/2 w-10 h-10 bg-dark-100/80 backdrop-blur-sm rounded-full flex items-center justify-center text-dark-900 hover:bg-dark-100 transition-all duration-300 opacity-0 group-hover:opacity-100"
            >
              <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15 19l-7-7 7-7" />
              </svg>
            </button>

            <button
              onClick={nextImage}
              className="absolute right-4 top-1/2 -translate-y-1/2 w-10 h-10 bg-dark-100/80 backdrop-blur-sm rounded-full flex items-center justify-center text-dark-900 hover:bg-dark-100 transition-all duration-300 opacity-0 group-hover:opacity-100"
            >
              <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 5l7 7-7 7" />
              </svg>
            </button>
          </>
        )}

        {/* Image Indicators */}
        {project.images.length > 1 && (
          <div className="absolute bottom-4 left-1/2 -translate-x-1/2 flex space-x-2">
            {project.images.map((_, index) => (
              <button
                key={index}
                onClick={() => {
                  setCurrentImage(index);
                  setIsAutoPlaying(false);
                }}
                className={`h-2 rounded-full transition-all duration-300 ${
                  index === currentImage 
                    ? 'bg-black/80 w-6' 
                    : 'bg-black/30 w-2'
                }`}
              />
            ))}
          </div>
        )}
      </div>
    </motion.div>
  );
};

// Mobile project carousel component matching portfolio-1 design
const MobileProjectImageCarousel: React.FC<{ project: Project }> = ({ project }) => {
  const [currentImage, setCurrentImage] = useState(0);
  const { isMobile } = useResponsive();

  const nextImage = () => {
    setCurrentImage((prev) => (prev + 1) % project.images.length);
  };

  const prevImage = () => {
    setCurrentImage((prev) => (prev - 1 + project.images.length) % project.images.length);
  };

  return (
    <motion.div
      className="relative group"
      initial={{ opacity: 0, scale: 0.9 }}
      whileInView={{ opacity: 1, scale: 1 }}
      viewport={{ once: true }}
      transition={{ duration: 0.8 }}
      style={{
        height: isMobile ? '40vh' : '80vh',
        width: isMobile ? '25vh' : '45vh', // Portrait ratio based on height
      }}
    >
      <div className="relative overflow-hidden rounded-2xl bg-dark-300 h-full">
        <AnimatePresence mode="wait">
          <motion.img
            key={currentImage}
            src={project.images[currentImage]}
            alt={`${project.title} - Image ${currentImage + 1}`}
            className="w-full h-full object-contain"
            initial={{ opacity: 0, scale: 1.1 }}
            animate={{ opacity: 1, scale: 1 }}
            exit={{ opacity: 0, scale: 0.9 }}
            transition={{ duration: 0.5 }}
          />
        </AnimatePresence>

        {/* Navigation Arrows - only show if multiple images */}
        {project.images.length > 1 && (
          <>
            {currentImage > 0 && (
              <button
                onClick={prevImage}
                className={`absolute left-2 top-1/2 -translate-y-1/2 ${isMobile ? 'w-8 h-8' : 'w-10 h-10'} bg-black/70 rounded-full flex items-center justify-center text-white/80 hover:bg-black/80 transition-all duration-300`}
              >
                <svg className={`${isMobile ? 'w-4 h-4' : 'w-5 h-5'}`} fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15 19l-7-7 7-7" />
                </svg>
              </button>
            )}

            {currentImage < project.images.length - 1 && (
              <button
                onClick={nextImage}
                className={`absolute right-2 top-1/2 -translate-y-1/2 ${isMobile ? 'w-8 h-8' : 'w-10 h-10'} bg-black/70 rounded-full flex items-center justify-center text-white/80 hover:bg-black/80 transition-all duration-300`}
              >
                <svg className={`${isMobile ? 'w-4 h-4' : 'w-5 h-5'}`} fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 5l7 7-7 7" />
                </svg>
              </button>
            )}
          </>
        )}
      </div>

      {/* Image Indicators - below the image */}
      {project.images.length > 1 && (
        <div className={`flex justify-center space-x-1 ${isMobile ? 'mt-3' : 'mt-4'}`}>
          {project.images.map((_, index) => (
            <button
              key={index}
              onClick={() => setCurrentImage(index)}
              className={`${isMobile ? 'h-1.5' : 'h-2'} rounded-full transition-all duration-300 ${
                index === currentImage 
                  ? 'bg-black/80' 
                  : 'bg-black/30'
              }`}
              style={{
                width: index === currentImage 
                  ? (isMobile ? '24px' : '32px') 
                  : (isMobile ? '6px' : '8px')
              }}
            />
          ))}
        </div>
      )}
    </motion.div>
  );
};
