import React, { useState, useRef, useEffect } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { CurtainLoader } from './components/loaders/CurtainLoader';
import { GlassNavbar } from './components/navigation/GlassNavbar';
import { ScrollTimelineIndicator } from './components/navigation/ScrollTimelineIndicator';
import { CursorRevealHero } from './components/sections/CursorRevealHero';
import { AboutSection } from './components/sections/AboutSection';
import { TimelineSection } from './components/sections/TimelineSection';
import { WorksSection } from './components/sections/WorksSection';
import { ContactSection } from './components/sections/ContactSection';
import { Footer } from './components/sections/Footer';
import { useMousePosition, useLoadingSequence } from './hooks';
import { smoothScroll } from './utils';

function App() {
  const [isLoading, setIsLoading] = useState(true);
  const { progress } = useLoadingSequence(3000);
  const mousePosition = useMousePosition();

  const sectionRefs = useRef<(HTMLElement | null)[]>([]);
  const sectionTitles = ['Hero', 'About', 'Timeline', 'Works', 'Contact'];

  const scrollToSection = (index: number) => {
    const targetSection = sectionRefs.current[index];
    if (targetSection) {
      const offsetTop = targetSection.offsetTop;
      smoothScroll(offsetTop - 80); // Account for navbar height
    }
  };

  const handleExplorePressed = () => {
    scrollToSection(3); // Go to Works section
  };

  const setSectionRef = (index: number) => (el: HTMLElement | null) => {
    sectionRefs.current[index] = el;
  };

  return (
    <div className="App relative min-h-screen bg-dark-100 text-dark-900 overflow-x-hidden">
      {/* Loading Screen */}
      <AnimatePresence>
        {isLoading && (
          <CurtainLoader
            onComplete={() => setIsLoading(false)}
            duration={3000}
          />
        )}
      </AnimatePresence>

      {/* Main Content */}
      <AnimatePresence>
        {!isLoading && (
          <motion.div
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            transition={{ duration: 0.5 }}
          >
            {/* Navigation */}
            <GlassNavbar onNavigationTap={scrollToSection} />

            {/* Scroll Timeline Indicator */}
            <ScrollTimelineIndicator
              sectionTitles={sectionTitles}
              onSectionTap={scrollToSection}
            />

            {/* Sections */}
            <main>
              {/* Hero Section */}
              <section ref={setSectionRef(0)} id="hero">
                <CursorRevealHero onExplorePressed={handleExplorePressed} />
              </section>

              {/* About Section */}
              <section ref={setSectionRef(1)} id="about">
                <AboutSection />
              </section>

              {/* Timeline Section */}
              <section ref={setSectionRef(2)} id="timeline">
                <TimelineSection />
              </section>

              {/* Works Section */}
              <section ref={setSectionRef(3)} id="works">
                <WorksSection />
              </section>

              {/* Contact Section */}
              <section ref={setSectionRef(4)} id="contact">
                <ContactSection />
              </section>

              {/* Footer */}
              <Footer />
            </main>

            {/* Noise Overlay */}
            <div className="fixed inset-0 pointer-events-none z-0 opacity-[0.02] bg-noise" />
          </motion.div>
        )}
      </AnimatePresence>

      {/* Custom Cursor Styles */}
      <style>{`
        :root {
          --mouse-x: ${mousePosition.x}px;
          --mouse-y: ${mousePosition.y}px;
        }
      `}</style>
    </div>
  );
}

export default App;
