import React, { useState, useRef, useEffect } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { useResponsive, useScrollProgress } from '../../hooks';
import { ScrollReveal } from '../animations/ScrollReveal';
import { projects } from '../../data';
import { Project } from '../../types';

export const WorksSection: React.FC = () => {
  const { isMobile } = useResponsive();
  const scrollProgress = useScrollProgress();
  const [headerScrollOffset, setHeaderScrollOffset] = useState(0);

  // Parallax effect for the header text
  useEffect(() => {
    const handleScroll = () => {
      const scrollY = window.scrollY;
      setHeaderScrollOffset(scrollY * 0.5);
    };

    window.addEventListener('scroll', handleScroll, { passive: true });
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  return (
    <section id="works" className="min-h-screen bg-dark-200">
      {/* Header with Scrolling Text */}
      <div className="relative h-screen overflow-hidden">
        <div 
          className="absolute inset-0 bg-cover bg-center opacity-20"
          style={{ backgroundImage: 'url(/api/placeholder/1920/1080)' }}
        />

        {/* Parallax Scrolling Text */}
        <div className="absolute inset-0 flex items-center justify-center overflow-hidden">
          <motion.div
            className="whitespace-nowrap text-dark-400 font-display font-black select-none"
            style={{
              fontSize: isMobile ? '80px' : '180px',
              transform: `translateX(${-headerScrollOffset}px)`,
            }}
          >
            DESIGNED WITH LOGIC DESIGNED WITH LOGIC DESIGNED WITH LOGIC
          </motion.div>
        </div>

        {/* Header Content */}
        <div className="relative z-10 flex flex-col justify-center h-full px-6">
          <div className="max-w-7xl mx-auto">
            <ScrollReveal>
              <h2 className="text-4xl md:text-6xl font-display font-bold text-dark-900 mb-6">
                Featured Works
              </h2>
            </ScrollReveal>

            <ScrollReveal delay={0.2}>
              <p className="text-xl text-dark-800 max-w-2xl">
                A collection of projects that showcase my passion for creating 
                digital experiences that are both beautiful and functional.
              </p>
            </ScrollReveal>
          </div>
        </div>
      </div>

      {/* Projects Grid */}
      <div className="px-6 py-20">
        <div className="max-w-7xl mx-auto space-y-32">
          {projects.map((project, index) => (
            <ProjectCard
              key={project.id}
              project={project}
              index={index}
              isReversed={index % 2 === 1}
            />
          ))}
        </div>
      </div>
    </section>
  );
};

interface ProjectCardProps {
  project: Project;
  index: number;
  isReversed: boolean;
}

const ProjectCard: React.FC<ProjectCardProps> = ({ project, index, isReversed }) => {
  const { isMobile, isDesktop } = useResponsive();

  if (isMobile) {
    return <MobileProjectCard project={project} index={index} />;
  }

  return (
    <ScrollReveal delay={index * 0.1}>
      <div className={`grid grid-cols-12 gap-12 items-center ${isReversed ? 'lg:gap-16' : ''}`}>
        {/* Project Info */}
        <div className={`col-span-12 lg:col-span-5 ${isReversed ? 'lg:order-2' : ''}`}>
          <motion.div
            className="space-y-6"
            initial={{ opacity: 0, x: isReversed ? 50 : -50 }}
            whileInView={{ opacity: 1, x: 0 }}
            viewport={{ once: true }}
            transition={{ duration: 0.8, ease: 'easeOut' }}
          >
            <div>
              <h3 className="text-3xl font-display font-bold text-dark-900 mb-4">
                {project.title}
              </h3>
              <p className="text-dark-800 leading-relaxed text-lg">
                {project.description}
              </p>
            </div>

            <div className="flex flex-wrap gap-2">
              {project.technologies.map((tech) => (
                <span
                  key={tech}
                  className="px-3 py-1 bg-dark-300 text-dark-800 text-sm rounded-full"
                >
                  {tech}
                </span>
              ))}
            </div>

            <div className="flex space-x-4">
              {project.liveUrl && (
                <motion.a
                  href={project.liveUrl}
                  target="_blank"
                  rel="noopener noreferrer"
                  className="btn-primary"
                  whileHover={{ scale: 1.05 }}
                  whileTap={{ scale: 0.95 }}
                >
                  View Live
                </motion.a>
              )}
              {project.githubUrl && (
                <motion.a
                  href={project.githubUrl}
                  target="_blank"
                  rel="noopener noreferrer"
                  className="btn-secondary"
                  whileHover={{ scale: 1.05 }}
                  whileTap={{ scale: 0.95 }}
                >
                  View Code
                </motion.a>
              )}
            </div>
          </motion.div>
        </div>

        {/* Project Images */}
        <div className={`col-span-12 lg:col-span-7 ${isReversed ? 'lg:order-1' : ''}`}>
          <ProjectImageCarousel project={project} />
        </div>
      </div>
    </ScrollReveal>
  );
};

const MobileProjectCard: React.FC<{ project: Project; index: number }> = ({ project, index }) => {
  return (
    <ScrollReveal delay={index * 0.1}>
      <div className="space-y-8">
        <ProjectImageCarousel project={project} />

        <div className="space-y-6">
          <div>
            <h3 className="text-2xl font-display font-bold text-dark-900 mb-3">
              {project.title}
            </h3>
            <p className="text-dark-800 leading-relaxed">
              {project.description}
            </p>
          </div>

          <div className="flex flex-wrap gap-2">
            {project.technologies.map((tech) => (
              <span
                key={tech}
                className="px-3 py-1 bg-dark-300 text-dark-800 text-sm rounded-full"
              >
                {tech}
              </span>
            ))}
          </div>

          <div className="flex flex-col sm:flex-row gap-3">
            {project.liveUrl && (
              <motion.a
                href={project.liveUrl}
                target="_blank"
                rel="noopener noreferrer"
                className="btn-primary text-center"
                whileHover={{ scale: 1.05 }}
                whileTap={{ scale: 0.95 }}
              >
                View Live
              </motion.a>
            )}
            {project.githubUrl && (
              <motion.a
                href={project.githubUrl}
                target="_blank"
                rel="noopener noreferrer"
                className="btn-secondary text-center"
                whileHover={{ scale: 1.05 }}
                whileTap={{ scale: 0.95 }}
              >
                View Code
              </motion.a>
            )}
          </div>
        </div>
      </div>
    </ScrollReveal>
  );
};

const ProjectImageCarousel: React.FC<{ project: Project }> = ({ project }) => {
  const [currentImage, setCurrentImage] = useState(0);
  const [isAutoPlaying, setIsAutoPlaying] = useState(true);
  const { isMobile } = useResponsive();

  useEffect(() => {
    if (!isAutoPlaying) return;

    const interval = setInterval(() => {
      setCurrentImage((prev) => (prev + 1) % project.images.length);
    }, 3000);

    return () => clearInterval(interval);
  }, [isAutoPlaying, project.images.length]);

  const nextImage = () => {
    setCurrentImage((prev) => (prev + 1) % project.images.length);
    setIsAutoPlaying(false);
  };

  const prevImage = () => {
    setCurrentImage((prev) => (prev - 1 + project.images.length) % project.images.length);
    setIsAutoPlaying(false);
  };

  return (
    <motion.div
      className="relative group"
      initial={{ opacity: 0, scale: 0.9 }}
      whileInView={{ opacity: 1, scale: 1 }}
      viewport={{ once: true }}
      transition={{ duration: 0.8 }}
      whileHover={{ scale: 1.02 }}
    >
      <div className="relative overflow-hidden rounded-2xl bg-dark-300">
        <AnimatePresence mode="wait">
          <motion.img
            key={currentImage}
            src={project.images[currentImage]}
            alt={`${project.title} - Image ${currentImage + 1}`}
            className={`w-full object-cover ${
              project.type === 'mobile' ? 'h-96 md:h-[600px]' : 'h-64 md:h-96'
            }`}
            initial={{ opacity: 0, scale: 1.1 }}
            animate={{ opacity: 1, scale: 1 }}
            exit={{ opacity: 0, scale: 0.9 }}
            transition={{ duration: 0.5 }}
          />
        </AnimatePresence>

        {/* Navigation Arrows */}
        {project.images.length > 1 && (
          <>
            <button
              onClick={prevImage}
              className="absolute left-4 top-1/2 -translate-y-1/2 w-10 h-10 bg-dark-100/80 backdrop-blur-sm rounded-full flex items-center justify-center text-dark-900 hover:bg-dark-100 transition-all duration-300 opacity-0 group-hover:opacity-100"
            >
              <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15 19l-7-7 7-7" />
              </svg>
            </button>

            <button
              onClick={nextImage}
              className="absolute right-4 top-1/2 -translate-y-1/2 w-10 h-10 bg-dark-100/80 backdrop-blur-sm rounded-full flex items-center justify-center text-dark-900 hover:bg-dark-100 transition-all duration-300 opacity-0 group-hover:opacity-100"
            >
              <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 5l7 7-7 7" />
              </svg>
            </button>
          </>
        )}

        {/* Image Indicators */}
        {project.images.length > 1 && (
          <div className="absolute bottom-4 left-1/2 -translate-x-1/2 flex space-x-2">
            {project.images.map((_, index) => (
              <button
                key={index}
                onClick={() => {
                  setCurrentImage(index);
                  setIsAutoPlaying(false);
                }}
                className={`w-2 h-2 rounded-full transition-all duration-300 ${
                  index === currentImage ? 'bg-accent w-6' : 'bg-dark-600'
                }`}
              />
            ))}
          </div>
        )}
      </div>
    </motion.div>
  );
};
